CustomField.formula:
(
  (cg_Unit_Price__c * cg_Quantity__c)
  -
  (
    /* Discount by percent (if present) */
    (cg_Unit_Price__c * cg_Quantity__c) * 
    (IF(ISBLANK(cg_Discount_percent__c), 0, cg_Discount_percent__c / 100))
    +
    /* Discount by amount (if present) */
    IF(ISBLANK(cg_Discount_Amount__c), 0, cg_Discount_Amount__c)
  )
)
